import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react'
import randomColor from 'randomcolor'

export default function Playground() {
    const [count, setCount] = useState(50); // useState has two parameters 1) state and 2) function     

    const inputRef = useRef()

    const [color, setColor] = useState(null); // useState has two parameters 1) state and 2) function 



    useEffect(() => {
        setColor(randomColor(randomColor()));
        inputRef.current.focus();
    }, [count])


    const calculate = useCallback(<Calculate/>,[count])

    return (
        <div>
            <div style={{ border: `10px solid ${color}`, width: '200px' }}></div>
            <div>
                {count}
                <button onClick={() => setCount(currentCount => currentCount - 1)}>-</button> {/* by function || two inline function */}
                <button onClick={() => setCount(currentCount => currentCount + 1)}>+</button>
                <button onClick={() => setColor(randomColor => randomColor + 1)}>Set Colour</button>
                {/* <button  onClick={()=> setCount(count=>count - 1)}>+</button>  
            <button  onClick={()=> setCount(count=>count + 1)}>+</button> */} {/*by value*/}
                <hr />
                <input ref={inputRef} type="range" onChange={e => setCount(e.target.value)} value={count} />
            </div>
            <Calculate/>
        </div>
    )
}
function Calculate() {
    const renderCount = useRef(1);
    return <div>{renderCount.current++}</div>
}
