export default function DataLoader() {
    const [data, setData] = useState([]);
    useEffect(() => {
        fetch("http://localhost:3001/links/")
            .then(response => response.json())
            .then(data => setData(data));
    });
    return (
        <div>
            <ul>
                {data.map(el => (
                    <li key={el.id}>{el.title}</li>
                ))}
            </ul>
        </div>
    );
}  


